// A. REGISTER THE MODULE +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++

var workController = angular.module('workController', []);

// A. END +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++

// B. CONTROLLER ++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++

workController.controller(
    'ctrlWork', [
        
        '$scope',
        '$state',
        '$http',
        '$rootScope',
        '$location',
        function ($scope, $state, $http, $rootScope, $location) { 
            
            // B.1. HELPER VARIABLES ----------------------------------------------------------------------------------
            
            $scope.snippets = 'assets/includes/snippets/';
            $scope.sections = 'assets/includes/sections/';
            $scope.icon = 'assets/images/icons';
            

            // B.2. MOBILE LISTENER -----------------------------------------------------------------------------------

            var screen = $( window ).width();
            console.log('I am a screen, and my width is: ' + screen);
            console.log('and we are in Work Controller');

            // B.2. END -----------------------------------------------------------------------------------------------

            // B.3. ANCHOR SCROLL -------------------------------------------------------------------------------------
            

            // B.3. END -----------------------------------------------------------------------------------------------

        }
    ]
);

// B. END +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++

// END OF FILE ========================================================================================================